    List<String> name = new ArrayList<String>();

    final List<OvergroundStop> overgroundStops = Arrays.asList(
            gson.fromJson(json, OvergroundStop[].class)
    );

    public void onCreate(final Bundle savedInstanceState){
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_overground);


       for(int i = 0; i < overgroundStops.size(); i++) {
           name.add(overgroundStops.get(i).name);
       }


        overgroundListView = (ListView) findViewById(R.id.months_list);
        arrayAdapter = new ArrayAdapter(this, android.R.layout.simple_list_item_1, name);
        overgroundListView.setAdapter(arrayAdapter);

        overgroundListView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> adapterView, View view, int i, long l) {
                Intent intent = new Intent(OvergroundStartActivity.this, MainActivity.class);
                intent.putExtra("valueStartPlacement", Integer.toString(i));
                startActivity(intent);
            }
        });
    }
